// import logo from './logo.svg';
import './App.css';
// import Barchart from './components/Barchart.js'
import * as d3 from 'd3';
import data from './components/data'
import React from 'react';





function Chart() {

    const margin = {top: 40, bottom: 10, left: 120, right: 20};
    const width = 800 - margin.left - margin.right;
    const height = 600 - margin.top - margin.bottom;

    // Creates sources <svg> element
    const svg = d3.select('body').append('svg')
                .attr('width', width+margin.left+margin.right)
                .attr('height', height+margin.top+margin.bottom);

    // Group used to enforce margin
    const g = svg.append('g')
                .attr('transform', `translate(${margin.left},${margin.top})`);

    // Global variable for all data
    const data = [66.38, 21.51, 23.37, 34.17, 36.21];

    const bar_height = 50;

    /////////////////////////
    // TODO create a nested title element that shows the value as tooltip

    // Render the chart with new data

    // DATA JOIN
    const rect = g.selectAll('rect').data(data).join(
      // ENTER 
      // new elements
      (enter) => enter.append('rect').attr('x', 0),
      // UPDATE
      // update existing elements
      (update) => update,
      // EXIT
      // elements that aren't associated with data
      (exit) => exit.remove()
      );

    // ENTER + UPDATE
    // both old and new elements
    rect
      .attr('height', bar_height)
      .attr('width', (d) => d * 8)
      .attr('y', (d, i) => i*(bar_height+5));
    return (

        <body>
            <h1>Bar chart mandemt</h1>
            <div id="filter">
                <h3>Filter hoe vaak iets mag voorkomen:</h3>
                <label id="range">
                    <p>0</p>
                    <p>1</p>
                    <p>2</p>
                    <p>3</p>
                    <p>4</p>
                    <p>5</p>
                    <p>6</p>
                    <p>7</p>
                    <p>8</p>
                    <p>9</p>
                    <p>10</p>
                </label>

                <input type="range" min="0" max="10" defaultValue='5' ></input>

                <select name="filter">
                    <option value="zin" selected>Hoeveel zin heb je in de techtrack?</option>
                    <option value="verdieping">Op welke verdieping studeer je het liefste?</option>
                    <option value="zuivelproduct">Wat is je favoriete zuivelproduct?</option>
                    <option value="oogkleur">Wat is je oogkleur?</option>
                    <option value="dier">Wat is je favoriete huisdier?</option>
                </select>

            </div>
            <main> </main>
        </body>

    )
}

export default Chart;
